import React, { useState } from 'react';

function App() {
  const [нормаЧасов, установитьНормуЧасов] = useState(167);
  const [оклад, установитьОклад] = useState(100000);
  const [нормаНочных, установитьНормуНочных] = useState(8); // Норма ночных часов
  const [массив1, установитьМассив1] = useState([9, 8, 8, 7, 4, 8, 12, 15, 4, 3, 9, 5, 4, 9, 9, 2, 7, 14, 8, 11, 3, 13, 12, 7, 9]);
  const [массив2, установитьМассив2] = useState([6, 7, 7, 2, 7, 7, 7, 7, 7, 6, 7, 7, 7, 7, 0, 7, 4, 7, 5, 6, 4, 6, 4, 6, 7]);
  const [результаты, установитьРезультаты] = useState(null);

  const рассчитатьИтоговуюЗП = () => {
    const новыеРезультаты = рассчитатьЗарплату(массив1, массив2, нормаЧасов, оклад, нормаНочных);
    установитьРезультаты(новыеРезультаты);
  };

  const handleInputChange = (event, setState) => {
    const { value } = event.target;
    setState(value);
  };

  return (
    <div className="App list">
      <label>
        Норма часов:
        <input type="number" value={нормаЧасов} onChange={(e) => handleInputChange(e, установитьНормуЧасов)} />
      </label>

      <label>
        Оклад:
        <input type="number" value={оклад} onChange={(e) => handleInputChange(e, установитьОклад)} />
      </label>

      <label>
        Норма ночных часов:
        <input type="number" value={нормаНочных} onChange={(e) => handleInputChange(e, установитьНормуНочных)} />
      </label>

      <label>
        Дневные часы через запятую:
        <input type="text" value={массив1.join(',')} onChange={(e) => handleInputChange(e, установитьМассив1)} />
      </label>

      <label>
        Ночные часы через запятую:
        <input type="text" value={массив2.join(',')} onChange={(e) => handleInputChange(e, установитьМассив2)} />
      </label>

      <button onClick={рассчитатьИтоговуюЗП}>Рассчитать</button>

      {/* Вывод результатов */}
      {результаты && (
        <div>
          <p>Норма часов: {нормаЧасов}</p>
          <p>Общее количество переработок: {результаты.количествоПереработок}</p>
          <p>Норма дневных (часов): {результаты.суммаПервогоМассива}</p>
          <p>Норма ночных (часов): {результаты.суммаИзВторогоМассива}</p>
          <p>Оставшиеся часы дневных: {результаты.оставшиеЧасыМассив1}</p>
          <p>Оставшиеся часы ночных: {результаты.оставшиеЧасыМассив2}</p>
          <p>Сумма оставшихся часов в массиве 1: {результаты.суммаОставшихсяЧасовМассив1}</p>
          <p>Сумма оставшихся часов в массиве 2: {результаты.суммаОставшихсяЧасовМассив2}</p>
          <p>Ночные часы без переработок: {результаты.ночныеЧасыБезПереработок}</p>
          <p>Итого часов: {результаты.итого}</p>
          <p>Цена часа: {результаты.ценаЧаса}</p>
          <p>Цена часа переработки: {результаты.ценаЧасаПереработки}</p>
        </div>
      )}
    </div>
  );
}

export default App;

function рассчитатьЗарплату(массив1, массив2, нормаЧасов, оклад, нормаНочных) {
  let общаяСумма = 0;
  let суммаПервогоМассива = 0;
  let суммаИзВторогоМассива = 0;
  let оставшиеЧасыМассив1 = 0;
  let оставшиеЧасыМассив2 = 0;
  let суммаОставшихсяЧасовМассив1 = 0;
  let суммаОставшихсяЧасовМассив2 = 0;

  let i = 0;
  let j = 0;

  while (i < массив1.length || j < массив2.length) {
    if (i < массив1.length) {
      общаяСумма += массив1[i];
      суммаПервогоМассива += массив1[i];
      оставшиеЧасыМассив1 += массив1[i];
      суммаОставшихсяЧасовМассив1 += массив1[i];
      i++;
    }

    if (общаяСумма >= нормаЧасов) {
      break;
    }

    if (j < массив2.length) {
      общаяСумма += массив2[j];
      суммаИзВторогоМассива += массив2[j];
      оставшиеЧасыМассив2 += массив2[j];
      суммаОставшихсяЧасовМассив2 += массив2[j];
      j++;
    }

    if (общаяСумма >= нормаЧасов) {
      break;
    }
  }

  // Вычисляем ночные часы без переработок
  const ночныеЧасыБезПереработок = Math.max(0, нормаНочных * 1.2 - нормаНочных);

  const итого = массив1.concat(массив2).reduce((acc, curr) => acc + curr, 0);
  const оставшаяСумма = Math.max(0, общаяСумма - нормаЧасов);
  const количествоПереработок = оставшаяСумма;

  const ценаЧаса = оклад / нормаЧасов;
  const ценаЧасаПереработки = ценаЧаса * 1.5; // Пусть переработка оплачивается с коэффициентом 1.5

  return {
    общаяСумма,
    суммаПервогоМассива,
    суммаИзВторогоМассива,
    оставшаяСумма,
    количествоПереработок,
    итого,
    ценаЧаса,
    ценаЧасаПереработки,
    оставшиеЧасыМассив1,
    оставшиеЧасыМассив2,
    суммаОставшихсяЧасовМассив1,
    суммаОставшихсяЧасовМассив2,
    ночныеЧасыБезПереработок
  };
}
