      суммаПервогоМассива += массив1[i];
      суммаИзВторогоМассива += массив2[i];

      if (общаяСумма >= нормаЧасов) {
          достигнута = i + 1;
          break;
      }
  }

  const оставшаяСумма = общаяСумма - нормаЧасов;

  return {
      общаяСумма,
      достигнута,
      суммаПервогоМассива,
